mapper.select:
select cno, cstatus, crank, clno, email, join_dt
from clubmember
where clno = #{clno}
order by crank desc;

select email
from clubmember
where clno = #{clno}

select cno, cstatus, crank, clno, email, join_dt
from clubmember
where clno = #{clno} and email = #{email}

mapper.update:
update clubmember
set cstatus = #{cstatus}
where clno = #{clno} and email = #{email}

mapper.select:
select cno, cstatus, crank, clno, email, join_dt
from clubmember
where clno = #{clno} and crank = #{crank}
