mapper.select:
select email, name, nickname, pw, tel, image, auth, join_dt
from member
where email = #{email}

 		SELECT email, name, nickname, pw, tel, image, auth, join_dt
		FROM member
		WHERE nickname = #{nickname}

select email
from member
where name = #{name}
and tel = #{tel}

mapper.update:
update member
set pw = #{pw}
where email = #{email}

update member
set name = #{name}, pw = #{pw}, nickname = #{nickname}, tel = #{tel}, image = #{image}
where email = #{email}
